import React, { useState } from 'react';
import './DataTable.css';

const DataTable = () => {
  const [tableData, setTableData] = useState([

  ]);

  const roommateData = [
    {
      name: 'Roommate 1',
      totalSpent: 0
    },
    {
      name: 'Roommate 2',
      totalSpent: 0
    },
    {
      name: 'Roommate 3',
      totalSpent: 0
    },
  ];


  const [modalOpen, setModalOpen] = useState(false);

  const [updateModalOpen, setUpdateModalOpen] = useState(false);

  const [formData, setFormData] = useState({
    date: '',
    name: '',
    owner: '',
    description: '',
    amount: '',
    status: '',
    receipt: ''
  });

  const [currentIndex, setCurrentIndex] = useState(-1);

  const handleModalOpen = () => {
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  const handleReceiptChange = event => {
    setFormData({
      ...formData,
      receipt: event.target.files[0]
    });
  };


  const handleDeleteBill = () => {
    const updatedTableData = [...tableData];
    updatedTableData.splice(currentIndex, 1);
    setTableData(updatedTableData);
    setUpdateModalOpen(false);
  };

  const handleUpdateModalOpen = id => {
    const billIndex = tableData.findIndex(bill => bill.id === id);
    setCurrentIndex(billIndex);
    setFormData(tableData[billIndex]);
    setUpdateModalOpen(true);
  };


  const handleUpdateModalClose = () => {
    setUpdateModalOpen(false);
  };

  const handleFormDataChange = event => {
    setFormData({
      ...formData,
      date: new Date().toLocaleDateString(), //keep?
      [event.target.name]: event.target.value
    });
  };

  const handleAddBill = () => {
    const newBill = {
      id: tableData.length + 1,
      date: formData.date.toLocaleDateString(),
      name: formData.name,
      owner: formData.owner,
      description: formData.description,
      amount: formData.amount,
      status: formData.status,
      receipt: formData.receipt
    };

    setTableData([...tableData, newBill]);
    setModalOpen(false);
  };

  // const handleAddBill = () => {
  //   const formData = new FormData();
  //   formData.append("date", formData.date);
  //   formData.append("name", formData.name);
  //   formData.append("owner", formData.owner);
  //   formData.append("description", formData.description);
  //   formData.append("amount", formData.amount);
  //   formData.append("status", formData.status);
  //   formData.append("receipt", formData.receipt);

  //   // send the form data to the server here

  //   setTableData([...tableData, newBill]);
  //   setModalOpen(false);
  // };

  const handleUpdateBill = () => {
    const updatedTableData = [...tableData];
    updatedTableData[currentIndex] = {
      ...formData
    };

    setTableData(updatedTableData);
    setUpdateModalOpen(false);

  };

  const data1 = [

  ];

  const data2 = [

  ];


  const handleTable1 = () => {
    setTableData(data1);
  };

  const handleTable2 = () => {
    setTableData(data2);
  };

  return (
    <div className="datatable">
      {updateModalOpen && (
  <div className="modal">
    <div className="modal-content">
      <span className="close" onClick={handleUpdateModalClose}>&times;</span>

      <form>
        <input
          type="text"
          name="date"
          placeholder="Date"
          value={formData.date}
          onChange={handleFormDataChange}
        />
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleFormDataChange}
        />
        <input
          type="text"
          name="owner"
          placeholder="Owner"
          value={formData.owner}
          onChange={handleFormDataChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleFormDataChange}
        />
        <input
          type="text"
          name="amount"
          placeholder="Amount"
          value={formData.amount}
          onChange={handleFormDataChange}
        />
        <input
          type="text"
          name="status"
          placeholder="Status"
          value={formData.status}
          onChange={handleFormDataChange}
        />
        <input
          type="file"
          name="receipt"
          placeholder="Upload Receipt"
          onChange={handleReceiptChange}
          />
      </form>
      <button onClick={handleUpdateBill}>Save</button>
      <button onClick={handleDeleteBill}>Delete</button>
    </div>
  </div>
)}

      <div className="buttons">
        <button onClick={handleTable1}>Table 1</button>
        <button onClick={handleTable2}>Table 2</button>
        <button onClick={handleModalOpen}>Add Bill</button>
      </div>
      {modalOpen && (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={handleModalClose}>&times;</span>
        <form>
          <input
            type="text"
            name="date"
            placeholder="Date"
            value={formData.date}
            onChange={handleFormDataChange}
          />
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleFormDataChange}
          />
          <input
            type="text"
            name="owner"
            placeholder="Owner"
            value={formData.owner}
            onChange={handleFormDataChange}
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleFormDataChange}
          />
          <input
            type="text"
            name="amount"
            placeholder="Amount"
            value={formData.amount}
            onChange={handleFormDataChange}
          />
          <div>
        <input
          type="radio"
          id="paid"
          name="status"
          value="paid"
          checked={formData.status === 'paid'}
          onChange={handleFormDataChange}
        />
        <label htmlFor="paid">Paid</label>
        <input
          type="radio"
          id="unpaid"
          name="status"
          value="unpaid"
          checked={formData.status === 'unpaid'}
          onChange={handleFormDataChange}
        />
        <label htmlFor="unpaid">Unpaid</label>
      </div>
          <input
            type="text"
            name="receipt"
            placeholder="Receipt"
            value={formData.receipt}
            onChange={handleFormDataChange}
          />
          <button onClick={handleAddBill}>Add Bill</button>
        </form>
      </div>
    </div>
  )}
      <table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Date</th>
      <th>Name</th>
      <th>Owner</th>
      <th>Description</th>
      <th>Amount</th>
      <th>Status</th>
      <th>Receipt</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
  {tableData.map((row, index) => (
  <tr key={row.id}>
        <td>{row.id}</td>
        <td>{row.date}</td>
        <td>{row.name}</td>
        <td>{row.owner}</td>
        <td>{row.description}</td>
        <td>{row.amount}</td>
        <td>{row.status}</td>
        <td>{row.receipt}</td>
        <td>
        <button onClick={() => handleUpdateModalOpen(row.id)}>Update</button>


    </td>

      </tr>
    ))}
  </tbody>
</table>

    </div>
  );
};

export default DataTable;
