import React, { useState } from 'react';
import './ColorCodedBar.css';

const ColorCodedBar = () => {
  const [data, setData] = useState({
    'Roommate 1': { color: 'red', value: 30 },
    'Roommate 2': { color: 'blue', value: 20 },
    'Roommate 3': { color: 'green', value: 50 },
  });

  const handleDataChange = (user, newValue) => {
    setData({
      ...data,
      [user]: {
        ...data[user],
        value: newValue,
      },
    });
  };

  const totalBudget = Object.values(data).reduce(
    (acc, item) => acc + item.value,
    0
  );
  const totalSpent = Object.values(data).reduce(
    (acc, item) => acc + item.value,
    0
  );
  const remainingBudget = totalBudget - totalSpent;
  const remainingBudgetPercentage = (remainingBudget / totalBudget) * 100;

  return (
    <div className="budget-bar">
      <div className="roommates">
        {Object.entries(data).map(([user, { color }]) => (
          <div key={user} className="roommate">
            <span className="roommate-color" style={{ backgroundColor: color }} />
            <span className="roommate-name">{user}</span>
          </div>
        ))}
        <div className="total-budget">
          Total Budget: {totalBudget}
        </div>
      </div>
      <div className="remaining-budget-bar">
        <div
          className="remaining-budget-item"
          style={{ width: `${remainingBudgetPercentage}%` }}
        />
      </div>
      <div className="bar-container">
        {Object.entries(data).map(([user, { color, value }]) => (
          <div
            key={user}
            className="bar-item"
            style={{
              backgroundColor: color,
              width: `${(value / totalBudget) * 100}%`,
            }}
          />
        ))}
      </div>
    </div>
  );
};

export default ColorCodedBar;
