import React, { useState } from 'react';
import './ColorCodedBar.css';

const ColorCodedBar = () => {
  const [data, setData] = useState([
    { color: 'red', value: 30 },
    { color: 'blue', value: 20 },
    { color: 'green', value: 50 },
  ]);

  return (
    <div className="color-coded-bar-container">
      <div className="color-coded-bar">
        {data.map((item, index) => (
          <div
            key={index}
            className="bar-item"
            style={{ backgroundColor: item.color, width: `${item.value}%` }}
          ></div>
        ))}
      </div>
    </div>
  );
};

export default ColorCodedBar;
