import React, { useState } from 'react';
import './ColorCodedBar.css';

const ColorCodedBar = () => {
  const [data, setData] = useState([
    { color: 'red', value: 30, name: 'Roommate 1' },
    { color: 'blue', value: 20, name: 'Roommate 2' },
    { color: 'green', value: 50, name: 'Roommate 3' },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [newTotalBudget, setNewTotalBudget] = useState(0);

  const handleSave = () => {
    setShowModal(false);
  };

  return (
    <div className="budget-bar">
      <div className="edit-budget-button" onClick={() => setShowModal(true)}>
        Edit Budget
      </div>
      {showModal && (
        <div className="edit-budget-modal">
          <input
            type="number"
            value={newTotalBudget}
            onChange={(e) => setNewTotalBudget(e.target.value)}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setShowModal(false)}>Cancel</button>
        </div>
      )}
      <div className="roommates">
        {data.map((item, index) => (
          <div key={index} className="roommate">
            <span className="roommate-color" style={{ backgroundColor: item.color }} />
            <span className="roommate-name">{item.name}</span>
          </div>
        ))}
      </div>
      <div className="bar-container">
        {data.map((item, index) => (
          <div
            key={index}
            className="bar-item"
            style={{
              backgroundColor: item.color,
              width: `${item.value}%`,
            }}
          >
            <span className="bar-item-value">{item.value}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ColorCodedBar;
