import React, { useState } from 'react';
import './ColorCodedBar.css';

const ColorCodedBar = () => {
  const [data, setData] = useState([
    { color: 'red', value: 30, name: 'Roommate 1' },
    { color: 'blue', value: 20, name: 'Roommate 2' },
    { color: 'green', value: 50, name: 'Roommate 3' },
  ]);

  const totalBudget = data.reduce((acc, item) => acc + item.value, 0);
  const totalSpent = data.reduce((acc, item) => acc + item.value, 0);
  const remainingBudget = totalBudget - totalSpent;
  const remainingBudgetPercentage = (remainingBudget / totalBudget) * 100;

  return (
    <div className="budget-bar">
      <div className="roommates">
        {data.map((item, index) => (
          <div key={index} className="roommate">
            <span className="roommate-color" style={{ backgroundColor: item.color }} />
            <span className="roommate-name">{item.name}</span>
          </div>
        ))}
        <div className="total-budget">
          Total Budget: {totalBudget}
        </div>
      </div>
      <div className="remaining-budget-bar">
        <div
          className="remaining-budget-item"
          style={{ width: `${remainingBudgetPercentage}%` }}
        />
      </div>
      <div className="bar-container">
        {data.map((item, index) => (
          <div
            key={index}
            className="bar-item"
            style={{
              backgroundColor: item.color,
              width: `${(item.value / totalBudget) * 100}%`,
            }}
          >
            <div className="bar-value">{item.value}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ColorCodedBar;
