import React from 'react';
import './ColorCodedBar.css';

const ColorCodedBar = ({ data }) => {
  return (
    <div className="color-bar">
      {data.map(d => (
        <div
          key={d.name}
          className="color-segment"
          style={{ backgroundColor: d.color, width: `${d.value}%` }}
        />
      ))}
    </div>
  );
};

export default ColorCodedBar;
