import React, { useState } from 'react';
import './BudgetBar.css';

const BudgetBar = () => {
  const [roommates, setRoommates] = useState([
    { name: 'Roommate 1', color: 'red', budget: 500, spent: 200 },
    { name: 'Roommate 2', color: 'blue', budget: 500, spent: 300 },
    { name: 'Roommate 3', color: 'green', budget: 500, spent: 100 },
  ]);

  const totalBudget = roommates.reduce((acc, curr) => acc + curr.budget, 0);
  const totalSpent = roommates.reduce((acc, curr) => acc + curr.spent, 0);
  const remainingBudget = totalBudget - totalSpent;

  return (
    <div className="budget-bar-container">
      <div className="roommates-container">
        {roommates.map((roommate, index) => (
          <div key={index} className="roommate">
            <div
              className="roommate-color"
              style={{ backgroundColor: roommate.color }}
            ></div>
            <div className="roommate-name">{roommate.name}</div>
          </div>
        ))}
        <div className="total-budget">Total Budget: {totalBudget}</div>
      </div>
      <div className="bars-container">
        <div className="total-spent-bar" style={{ width: `${(totalSpent / totalBudget) * 100}%` }}></div>
        <div className="remaining-budget-bar" style={{ width: `${(remainingBudget / totalBudget) * 100}%` }}></div>
      </div>
    </div>
  );
};

export default BudgetBar;
